import {
    useEffect, 
    useState,
} from 'react';
import { connect } from 'react-redux';
import { makeStyles } from '@material-ui/styles';
import Loading from './containers/Loading';
import Landing from './containers/Landing';
import Home from './containers/Home';
import { isLoggedIn } from './services/auth';
import { updateUserLoginState } from './redux/actions';

const useStyles = makeStyles({
    root: {
        // height: '100%',
        // width: '100%',
        // minWidth: 800,
        // overflow: 'auto',
        // position: 'relative',
        // fontFamily: '-apple-system,system-ui,BlinkMacSystemFont,Segoe UI,Roboto,Helvetica Neue,Fira Sans,Ubuntu,Oxygen,Oxygen Sans,Cantarell,Droid Sans,Apple Color Emoji,Segoe UI Emoji,Segoe UI Symbol,Lucida Grande,Helvetica,Arial,sans-serif',
    }
});

const App = ({
    Component,
    pageProps,
    uid,
    updateUserLoginState,
}) => {
    const [initialRender, setInitialRender] = useState(true);    
    const classes = useStyles();

console.log('main')


    useEffect(() => {
        isLoggedIn()
        .then(results => {
            updateUserLoginState(results);
            setInitialRender(false);
        })
        .catch(err => {
            setInitialRender(false);
        });

    }, []);

    if (initialRender) return <></>;

    return (
        <Component {...pageProps} />
        // <div className={classes.root}>
        //     <Loading />
        //     <Home/>
        //     {/* {uid
        //         ? (
        //             <Home/>
        //         )
        //         : (
        //             <Landing />
        //         )
        //     } */}
        // </div>
    );
};

const mapStateToProps = ({
    loading,
    uid,
}) => ({
    loading,
    uid,
});

const mapDispatchToProps = {
    updateUserLoginState,
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
