import {
    useEffect,
} from 'react';
import { connect } from 'react-redux';
import { makeStyles } from '@material-ui/styles';
import Loading from './containers/Loading';
import Landing from './containers/Landing';
import { isLoggedIn } from './services/auth';
import { updateUserLoginState } from './redux/actions';

const useStyles = makeStyles({
    root: {
        height: '100%',
        width: '100%',
        minWidth: 800,
        overflow: 'auto',
        position: 'relative',
        fontFamily: '-apple-system,system-ui,BlinkMacSystemFont,Segoe UI,Roboto,Helvetica Neue,Fira Sans,Ubuntu,Oxygen,Oxygen Sans,Cantarell,Droid Sans,Apple Color Emoji,Segoe UI Emoji,Segoe UI Symbol,Lucida Grande,Helvetica,Arial,sans-serif',
    }
});

const App = ({
    uid,
    updateUserLoginState,
}) => {
    const classes = useStyles();

    useEffect(async () => {
        const uid = await isLoggedIn()
        .then(results => results.data.uid)
        .catch(err => console.log(err));

        await updateUserLoginState(uid);
    }, []);

    return (
        <div className={classes.root}>
            <Loading />
            {uid
                ? (
                    <div>logged in</div>
                )
                : (
                    <Landing />
                )
            }
        </div>
    );
};

const mapStateToProps = ({
    uid,
}) => ({
    uid,
});

const mapDispatchToProps = {
    updateUserLoginState,
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
