import { useState } from 'react';
import { makeStyles } from '@material-ui/styles';
import { connect } from 'react-redux';
import axios from 'axios';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import {
    updateLoginState,
} from '../../redux/actions';

const useStyles = makeStyles({
    root: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    field: {
        width: '80%',
        marginTop: 10,
        marginBottom: 10,
    },
    button: {
        width: '70%',
        marginTop: 10,
        marginBottom: 10,
    },
    actionFieldContainer: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'space-evenly',
        marginBottom: 20,
    },
    actionField: {
        color: 'blue',
        marginTop: 5,
        marginBottom: 5,

        '&:hover': {
            textDecoration: 'underline',
            cursor: 'pointer',
        }
    },
    errorMsg: {
        color: 'red',
    }
})

const SignUp = ({
    updateLoginState,
}) => {
    const classes = useStyles();
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMsg, setErrorMsg] = useState('');

    const handleSignUp = e => {
        if (!firstName.length || !lastName.length || !email.length || !password.length) return setErrorMsg('Please enter all the fields!');

        axios.post('http://localhost:6969/api/v1/auth/signup', {
            firstName,
            lastName,
            email,
            password
        })
        .then(res => console.log(res))
        .catch(err => console.log(err));
    };

    return (
        <div className={classes.root}>
            <TextField
                className={classes.field}
                label='First Name*'
                variant='outlined'
                onChange={e => setFirstName(e.target.value)}
            />
            <TextField
                className={classes.field}
                label='Last Name*'
                variant='outlined'
                onChange={e => setLastName(e.target.value)}
            />
            <TextField
                className={classes.field}
                label='Email*'
                variant='outlined'
                onChange={e => setEmail(e.target.value)}
            />
            <TextField
                className={classes.field}
                label='Password*'
                variant='outlined'
                type='password'
                onChange={e => setPassword(e.target.value)}
            />
            <Button
                className={classes.button}
                variant='contained'
                color='primary'
                onClick={handleSignUp}
            >
                Sign Up
            </Button>
            <div className={classes.actionFieldContainer}>
                <div className={classes.errorMsg}>
                    {errorMsg}
                </div>
                <div 
                    className={classes.actionField}
                    onClick={() => updateLoginState('Sign In')}
                >
                    Sign In
                </div>
            </div>
        </div>
    );
};

const mapDispatchToProps = {
    updateLoginState,
};

export default connect(null, mapDispatchToProps)(SignUp);
