import {
    useEffect, 
    useRef,
} from 'react';
// import Router from 'next/router';
import { connect } from 'react-redux';
import { makeStyles } from '@material-ui/styles';
import Loading from './containers/Loading';
import Landing from './containers/Landing';
// import Home from './containers/Home';
import { isLoggedIn } from './services/auth';
import { updateUserLoginState } from './redux/actions';

const useStyles = makeStyles({
    root: {
        // height: '100%',
        // width: '100%',
        // minWidth: 800,
        // overflow: 'auto',
        // position: 'relative',
        // fontFamily: '-apple-system,system-ui,BlinkMacSystemFont,Segoe UI,Roboto,Helvetica Neue,Fira Sans,Ubuntu,Oxygen,Oxygen Sans,Cantarell,Droid Sans,Apple Color Emoji,Segoe UI Emoji,Segoe UI Symbol,Lucida Grande,Helvetica,Arial,sans-serif',
    }
});

const App = ({
    Component,
    pageProps,
    uid,
    updateUserLoginState,
}) => {
    const initialRender = useRef(true);    
    const classes = useStyles();

    useEffect(() => {
        isLoggedIn()
        .then(results => {
            updateUserLoginState(results);
            // setInitialRender(false);
            initialRender.current = false;
        })
        .catch(err => {
            // setInitialRender(false);
            // initialRender.current = false;

        });

    }, []);


    if (initialRender.current) return <></>;
    console.log('here', Component.WrappedComponent.name, uid, initialRender.current)


    if (uid) {
        return (        
            <div className={classes.root}>
                <Loading />
                <Component {...pageProps} />
            </div>
        );
    }
    else {
        // Router.push('/login');
        return <Landing/>
    }
};

const mapStateToProps = ({
    loading,
    uid,
}) => ({
    loading,
    uid,
});

const mapDispatchToProps = {
    updateUserLoginState,
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
