import * as types from './action-types';

const initialState = {
    loginState: 'Sign In',
    loading: false,
};

const mainReducer = (state = JSON.parse(JSON.stringify(initialState)), action) => {
    switch (action.type) {
        case types.UPDATE_LOADING:
            return {
                ...state,
                loading: action.loading,
            };

        case types.UPDATE_LOGIN_STATE:
            return {
                ...state,
                loginState: action.state,
            };

        case types.UPDATE_USER_LOGIN_STATE:
            console.log(action.uid, '<--- reducer')
            return {
                ...state,
                uid: action.uid,
            };

        default:
            return state;
    }
};

export default mainReducer
