import * as types from './action-types';

export const updateLoading = loading => ({
    type: types.UPDATE_LOADING,
    loading,
});

export const updateLoginState = state => ({
    type: types.UPDATE_LOGIN_STATE,
    state,
});

export const signIn = uid => ({
    type: types.SIGN_IN,
    uid,
});
