import axios from 'axios';
import path from './path';
// const path = `${process.env.REACT_APP_HOST}:${process.env.REACT_APP_PORT}/${process.env.REACT_APP_API_VERSION}/auth`;
// const path = `http://localhost:5000/api/v1/auth`;

const authPath = path + '/auth';

export const isLoggedIn = () => (
    axios
        .get(`${authPath}`)
        .then(results => results.data.uid)
        .catch(err => console.log(err))
);

export const signIn = (email, password) => (
    axios
        .post(`${authPath}/signin`, {
            email,
            password
        })
        .then(results => results.data.uid)
        .catch(err => console.log(err))
);
